INSERT INTO PEP_ANNOUNCEMENT(id, create_time, create_user_id, last_modify_time, last_modify_user_id, info_type, title, info, begin_time, end_time, info_status) VALUES ('90498kkl-95d6-11e6-83da-fbb35e195804', '2016-10-19 16:32:25.537', 'PEP_SYS', '2016-10-19 16:32:25.537', 'PEP_SYS', 'ACTIVITY_INFORMATION', '医改公告','&#12288;&#12288;按照国家和省深化医药卫生体制改革总体部署和要求，根据《关于印发<辽宁省省管公立医院医疗服务项目价格>的通知》（辽价发[2017]62号）和《沈阳市城市公立医院综合改革实施方案》精神，2017年8月26日零时，沈阳市将全面启动城市公立医院综合改革。作为省管大型公立医院，XXX医院以积极的态度、有为的担当和有力的措施，主动参加改革、投身改革，当好排头兵，努力发挥示范和引领作用。<br>&#12288;&#12288;8月26日起，无论是药品价格、诊查费用，还是检查检验项目价格、手术费用等都将会有所变化。但无论是降幅还是涨幅，XXX医院都将严格按照上级文件要求进行价格调整。在此期间，给患者朋友带来的不便，敬请谅解！<br><br>改革——<br>亮点一：方便快捷的医疗服务价格直通平台<br>&#12288;&#12288;XXX医院充分信息化优势，为患者开通方便快捷的医疗服务价格直通平台，在三个院区，通过公示展板和自助服务机详细了解医疗服务价格详情。<br><br>亮点二：医药价格改革咨询协调中心在您身边<br>&#12288;&#12288;在三个院区的门诊一楼大厅，分别设立了医药价格改革咨询协调中心，为患者提供医改政策咨询服务，保证患者能够明明白白就医。医院机关职能部门干部佩戴着醒目的胸牌，走到患者身边，担当义务引导员，为患者答疑解惑。<br><br>亮点三：确保医药价格改革关键时期服务质量<br>&#12288;&#12288;在医药价格改革的关键时期，XXX医院坚持首诊负责制，做到“三不停”，即节假日不停诊、检查设备24小时不停机，真心服务患者不停歇。\n <br>&#12288;&#12288;XXX医院针对可能出现的问题制定了解决方案并进行演练，确保改革期间医疗流程的顺畅和医疗秩序平稳，全院上下，团结一心，持续规范医疗质量管理，优化医疗服务流程，着力确保医疗质量安全。<br>&#12288;&#12288;XXX医院勇担公立医院公益大义，坚持——<br>&#12288;&#12288;"我们少休息一会儿，患者就少等待一会儿"，<br>&#12288;&#12288;"我们多专注一点儿，患者就多放心一点儿"，<br>&#12288;&#12288;"我们多用心一点儿，患者就多满意一点儿"，<br>&#12288;&#12288;"我们多奉献一点儿，患者就多受益一点儿"，<br><br>&#12288;&#12288;公立回归公益，改革不改初心。XXX医院全面推行公立医院综合改革，着力解决群众看病就医问题。在积极推行公立医院综合改革的进程中，XXX医院将勇担重任，为患者提供更加优质、高效、便捷的医疗服务。', '2016-12-01 00:00:00', '', 'Y');
